let panjangpassword = document.getElementById("panjangpassword"),
  password = document.getElementById("password");
function generatePassword(d) {
  let a = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()_+=-{}[]';:/?.,<>~`",
    b = "";
  for (let c = 0; c < d; c++) b += a[~~(Math.random() * a.length)];
  return b;
}
function getPassword() {
  let a = generatePassword(panjangpassword.value);
  password.value = a;
}
function savePassword() {
  (document.title = password.value), saveButton.setAttribute("href", "data:text/plain;charset=utf-8," + encodeURIComponent(`password saya: ${document.title}`)), saveButton.setAttribute("download", "MyPasswordGeneratorLOG.txt");
}
function copyPassword() {
  password.select(), document.execCommand("copy"), alert("Copied!");
}
